css:
.scrollable-search-results-item {
  padding: 0 10px;
}
.scrollable-search-results-summary {
  margin: 10px 0;
}
.scrollable-search-results-summary .warning {
  color: #e2c08d;
}
.scrollable-search-results-wrapper {
  display: flex;
  flex-direction: column;
  height: 100%;
  width: 100%;
}
.scrollable-search-results-controls {
  background: #282c34;
  position: relative;
}
.scrollable-search-results-controls-form {
  border-bottom: 1px solid #181a1f;
  display: flex;
  flex-wrap: wrap;
  justify-content: flex-end;
  padding: 10px 0 0 10px;
}
.scrollable-controls-block {
  align-items: center;
  display: flex;
  margin: 0 10px 10px 0;
}
.scrollable-controls-block atom-text-editor[mini] {
  min-width: 10em;
  width: 100%;
}
.scrollable-controls-block .select-list.popover-list {
  max-width: none;
  width: 100%;
}
.scrollable-controls-label {
  margin-right: 10px;
  text-align: right;
}
.scrollable-controls-label {
  min-width: 6em;
}
.scrollable-search-results-controls-button {
  margin-left: 10px;
}
.scrollable-search-results-controls-toggle {
  margin: 0 10px 10px 0;
}
.scrollable-search-results-controls-toggle-hidden {
  border-top: none;
  border-top-left-radius: 0;
  border-top-right-radius: 0;
  position: absolute;
  right: 10px;
  z-index: 1;
}
.scrollable-search-results {
  background: #282c34;
  flex-grow: 1;
  overflow: hidden;
}
